# -*- coding: utf-8 -*-
import base64
import io
import threading

LOCAL = False
INFORMATION_TOPIC = ""
LOCATION_X = 0.0
LOCATION_Y = 0.0

framerate = 60
quality = 100
res = (1280, 720)


def publish_information(local, topic, x, y):
    global LOCAL
    global INFORMATION_TOPIC
    global LOCATION_X
    global LOCATION_Y

    LOCAL = local
    INFORMATION_TOPIC = topic
    LOCATION_X = x
    LOCATION_Y = y
    import mqtt_client
    info_str = "{\"InformationMessage\":{" \
               + publish_battery() + "," \
               + publish_picture() + "," \
               + publish_location() + \
               "}}"
    print(info_str)
    mqtt_client.publish_on_topic(INFORMATION_TOPIC, info_str)


def publish_battery():
    bat = ""
    if not LOCAL:
        import brickpi3
        brickPi = brickpi3.BrickPi3()
        bat = brickPi.get_voltage_battery()
    else:
        bat = 9.3
    bat_str = "\"battery\":\"" + str(bat) + "\""
    return bat_str


def publish_picture():
    img_str = ""
    if not LOCAL:
        from picamera import PiCamera
        with PiCamera(framerate=framerate, resolution=res) as c:
            image = io.BytesIO()
            c.capture(image, 'jpeg', quality=quality, use_video_port=True)
            img_str = "\"picture\":\"" + "data:image/jpeg;base64," + base64.b64encode(image.getvalue()).decode() + "\""
    else:
        mock_img_str = ""
        img_str = "\"picture\":\"" + "data:image/jpeg;base64," + mock_img_str + "\""
    return img_str


def publish_location():
    loc_str = "\"x_coord\":" + str(LOCATION_X) + ",\"y_coord\":" + str(LOCATION_Y) + ""
    return loc_str
